query:
query items ($first: Int, $last: Int, $before: String, $after: String, $filter: Filter) {
    items (first: $first, last: $last, before: $before, after: $after, filter: $filter) {
        edges {
            cursor
            node {
                id
                brand {
                    id
                    title
                    items {
                        id
                        createdAt
                        description
                        isDisplayed
                        itemClass
                        presentedAvailableQty
                        sku
                        title
                        updatedAt
                    }
                }
                createdAt
                description
                dimension {
                    width
                    length
                    height
                    measurementUnit {
                        id
                        sid
                        title
                        titleAbbr
                        measurementSystem
                        measurementType
                    }
                }
                isDisplayed
                itemClass
                presentedAvailableQty
                product {
                    id
                    title
                    description
                    isDisplayed
                    attributes {
                        id
                        title
                        fieldtype
                        values
                    }
                    categories {
                        id
                        title
                    }
                }
                prices {
                    id
                    priceAmount {
                        currencyCode
                        amount
                    }
                    quantityMinimum
                    itemId
                }
                shippingInfo {
                    shippingFee {
                        currencyCode
                        amount
                    }
                    shippingWeight
                }

            }
        }
    }
}
